INSERT INTO `myImages` (`Filename`, `sightId`, `CaveName`, `Title`, `Titel`, `Description`, `Beschreibung`, `Photographer`, `PhotographerLink`, `Copyright`, `CopyrightLink`) VALUES
('244-092.jpg', NULL, 'Steinkirche', 'Courtyard', 'Vorplatz', 'The courtyard in front of the Steinkirche.\r\nThis place has a triangular shape.\r\nThe straight boundaries of the courtyard and the even walls show heavy human alterations.\r\nThis is a result of the crusades, as the cave was transformed into a replica of the Garden Tomb in Jerusalem.', 'Der Vorplatz der Steinkirche hat eine dreieckige  Form.\r\nAn seinen geradlinigen Rändern und den bearbeiteten Wänden kann man den menschlichen Einfluß deutlich erkennen.\r\nDiese Umbauten wurden zur Zeit der Kreuzzüge gemacht, als die Höhle in eine Nachbildung des Felsengrabs in Jerusalem umgewandelt wurde.', 'Jochen Duckeck', NULL, NULL, NULL),
('244-118.jpg', NULL, 'Steinkirche', 'Courtyard', 'Vorplatz', 'The courtyard in front of the Steinkirche.\r\nThis place has a triangular shape.\r\nThe straight boundaries of the courtyard and the even walls show heavy human alterations.\r\nThis is a result of the crusades, as the cave was transformed into a replica of the Garden Tomb in Jerusalem.', 'Der Vorplatz der Steinkirche hat eine dreieckige  Form.\r\nAn seinen geradlinigen Rändern und den bearbeiteten Wänden kann man den menschlichen EInfluß deutlich erkennen.\r\nDiese Umbauten wurden zur Zeit der Kreuzzüge gemacht, als die Höhle in eine Nachbildung des Felsengrabs in Jerusalem umgewandelt wurde.', 'Jochen Duckeck', NULL, NULL, NULL),
('244-095.jpg', NULL, 'Steinkirche', 'The Geologic SItuation', 'Die geologische Situation', 'A plate on the courtyard explains the geologic situation of the cave.', 'Eine Tafel am Vorplatz erklärt den geologischen Aufbau des Gebiets.', 'Jochen Duckeck', NULL, NULL, NULL),
('244-103.jpg', NULL, 'Steinkirche', 'Inside the Cave', 'Im Inndern der Höhle', 'The cave is a single chamber with nearly rectangular shape. \r\nSeveral walls show human alterations.', 'Die Höhle besteht aus einem einzigen großen Raum, der einen fast rechtwinkligen Grundriß besitzt. \r\nDie Wände zeigen vielfach menschliche Überarbeitung.', 'Jochen Duckeck', NULL, NULL, NULL),
('244-096.jpg', NULL, 'Steinkirche', 'The Cave Entrance', 'Der Höhleneingang', 'The entrance to the cave is also heavily alterated by man.\r\nMany of the works can be explaind with a wooden door, some holes may have hold the joints. One big excavation is called <b>Kanzel</b> (cancellus) in an attemt to proof the usage as a church. However, this cancellus seems not very functionally.', 'Der Eingang zur Höhle wurde sehr stark überarbeitet.\r\nDie Modifikationen können recht gut mit einem ehemals vorhandenen hölzernen Tor erklärt werden. EInige Hohläume wären geeignet die Scharniere aufzunehmen.\r\nEine größere Höhlung wird als Kanzel bezeichnet, was wohl auch eine Nutzung als Kirche nahelegen soll. Allerdings scheint diese Kanzel etwas unpraktisch zu sein.', 'Jochen Duckeck', NULL, NULL, NULL),
('007-016.jpg', NULL, 'Schillingsloch - Schillerhöhle', 'A Passage', 'Ein Höhlengang', 'The main passage of the Schillerhöhle is easy to visit and rather huge.\r\nDripstones, if they ever existed, were removed during the centuries.\r\nArcheologic finds are not recorded.', 'Der Hauptgang der Schillerhöhle ist leicht zu begehen und großräumig.\r\nTropfsteinschmuck, soweit er vorhanden war, ist im Laufe der Jahrzehnte verschwunden.', 'Jochen Duckeck', NULL, NULL, NULL),
('D008-078.jpg', NULL, 'Laichinger Tiefenhöhle', 'Kleine Halle (Small Hall)', 'Die Kleine Halle', 'This is the lowest point of the tour through Laichingen Vertical Cave.\r\nIt is 55m below surface.\r\nSee a photographer’s back!', 'Dies ist der tiefste Punkt der Höhlenführung, 55m unter der Erdoberfläche.', 'Jochen Duckeck', NULL, NULL, NULL),
('D008-111.jpg', NULL, 'Laichinger Tiefenhöhle', 'Nasser Schacht (Wet Shaft)', 'Nasser Schacht', 'It is easy to under stand why this shaft is called wet shaft: there is allways a lot of water dripping down.\r\nAnd during heavy rains the amount of water increases after only 15 minutes.\r\nThere seems to be a direct connection to the surface.', 'Es ist einfach zu verstiehen, warum dieser Schacht so heißt: die Tropfwassermeng ist heir ganzjährig sehr hoch.\r\nAußerdem machen sich Regenfälle an der Erdoberfläche bereits nach 15 Minuten bemerkbar, was für eine sehr direkte Verbindung spricht.', 'Jochen Duckeck', NULL, NULL, NULL),
('D008-070.jpg', NULL, 'Laichinger Tiefenhöhle', 'Große Halle (Great Hall)', 'Große Halle', 'This is the biggest chamber, 45m below the surface.\r\nVery impressive are the huge rocks, debris from the ceiling of the cavern.\r\nThe biggest one is 45 tons.', 'Dies ist der größte Raum der Höhle auf 45m Tiefe. Er beeindruckt durch die mächtigen Versturzblöcke, deren größter etwa 45 Tonnen schwer ist.', 'Jochen Duckeck', '', '', ''),
('020-068.jpg', NULL, 'Teufelshöhle', 'Crucifixion scene', 'Kreuzigungsgruppe', 'This formation resembles the crucifixion scene, with the three large Stalagmite representing the three crosses, and the small stalagmites in front of them representing the visiting people.', 'Die drei großen Tropfsteine stellen die drei Kreuzstämme der Kreuzigungsgruppe, die kleinen Stalagmiten davor das zuschauende Volk dar.', 'Jochen Duckeck', NULL, NULL, NULL),
('255-134.jpg', NULL, 'Bären- und Karlshöhle', 'Cave Bear (<i>Ursus spelaeus</i>)', 'Höhlenbär (<i>Ursus spelaeus</i>)', 'This skeleton of a <b>cave bear</b> (<i>Ursus spelaeus</i>) is one of dozens found in the cave.\r\nOthers are in the collection of several museums of the area.\r\nMost bones are still in their original location.', 'Dieses Skelett des <b>Höhlenbären</b> (<i>Ursus spelaeus</i>) ist eines von Dutzenden, die hier in der Höhle gefunden wurden.\r\nEinige sind in den Sammlungen süddeutscher Museen gelandet, aber die meisten sind immer noch an ihrem Fundort in der Höhle.', 'Jochen Duckeck', NULL, NULL, NULL),
('030-457.jpg', NULL, 'Jewel Cave', 'Dogtooth Spars', '', 'An intersection through the dogtooth spars shows us their structure.\r\nHuge calcite crystals growing perpendicular out of the wall. The dogtooths are the pointy end of an crystal, which is about 10cm long.', NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('255-138.jpg', NULL, 'Bären- und Karlshöhle', 'The Big Hall of Bears’ Bones', 'Die große Halle der Bärenknochen', 'To protect the visitors from the wild cave bears, a two meter high fence was erected.', 'Zum Schutz der Besucher vor wilden Höhlenbären wurde ein zwei Meter hoher Schutzzaun errichtet', 'Jochen Duckeck', NULL, NULL, NULL),
('255-108.jpg', NULL, 'Bären- und Karlshöhle', 'Patterns at the Ceiling', 'Muster an der Decke', 'This circular shapes at the ceiling are said to be the remains of stalactites, which were destroyed almost completely by cave ice during the Ice Age.', 'Kreisförmige Muster an der Decke werden als Reste von Tropfsteinen interpretiert, die währen der Eiszeit durch Höhleneis abgebrochen wurden.', 'Jochen Duckeck', NULL, NULL, NULL),
('030-455.jpg', NULL, 'Jewel Cave', 'Dogtooth Spars', '', 'A cleft with all the walls covered by dogtooth spars.\r\nThis is one of the most intensively decorated spots visited during a tour. The whole passage is covered seamless by calcite crystals.', NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('213-005.jpg', NULL, 'Sontheimer Höhle', 'The Entrance Building', 'Das Eingangsgebäude', 'The entrance building from 1971, with a ticket office and a small bar.', 'Das Eingangsgebäude von 1971 mit Kasse und Gasthaus.', 'Jochen Duckeck', NULL, NULL, NULL),
('013-112.jpg', NULL, 'Rakov Škocjan Nature Reserve', 'Veliki naravni most', '', 'A natural bridge called <b>Veliki naravni most</b>, the \"big\" natural bridge.', '', 'Jochen Duckeck', '', '', ''),
('159-816.jpg', NULL, 'Hellfire Corner', 'The Secret Wartime Tunnels', '', 'Inside Hellfire Corner', NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('274-028.jpg', NULL, 'Kalkberghöhlen', 'Historic Commemorative', 'Historische Gedenktafel', 'The former quarry was transformed into an open air arena called \"Thingplatz\" during the 3rd Reich. This Re', 'Während des 3. Reich wurde der Steinbruch als \"Thingplatz\" ausgebaut.', 'Jochen Duckeck', NULL, NULL, NULL),
('255-122.jpg', NULL, 'Bären- und Karlshöhle', 'Passage', 'Verbindungsgang', 'The hidden connection from the Karls Cave to the Bärenhöhle.', 'Die versteckte Verbindung von der Karlshöhle zur Bärenhöhle.', 'Jochen Duckeck', NULL, NULL, NULL),
('255-103.jpg', NULL, 'Bären- und Karlshöhle', 'Stalaktites', 'Stalaktiten', 'Stalactites with a strange shape.', 'Stalaktiten mit einer außergewöhnlichen Form.', 'Jochen Duckeck', NULL, NULL, NULL),
('255-118.jpg', NULL, 'Bären- und Karlshöhle', 'Stalagmite', 'Stalagmit', 'a really nice stalagmite.', 'Besondes schöner Stalagmit.', 'Jochen Duckeck', NULL, NULL, NULL),
('255-140.jpg', NULL, 'Bären- und Karlshöhle', 'Bears’ Cave', 'Bärenhöhle', 'Remarkable flowstones in the Bears’ Cave.\r\n', 'Auch in der Bärenhöhle gibt es bemerkenswerte Tropfsteine.\r\n', 'Jochen Duckeck', NULL, NULL, NULL),
('010-033.jpg', NULL, 'Ofnethöhlen', 'Big Ofnet Cave', 'Große Ofnethöhle', 'The entrance of the Big Ofnet Cave.', 'Eingang der Großen Ofnethöhle.', 'Jochen Duckeck', NULL, NULL, NULL),
('255-033.jpg', NULL, 'Nebelhöhle', 'Fog Cave - Last Hall', 'Letzte Halle', 'The last hall of the cave is very huge, but has very little formations.\r\nOn times of high visitor numbers, the tours leave the cave here. Otherwise visitors make selfguided tourse and go back the same path through the cave.', 'Die letzte Halle ist recht groß, besitzt aber kaum Tropfsteine.\r\nBei großem Besucherandrang verlassen die Führungen hier die Höhle. Ansonsten wird die Höhle ohne Führung besichtigt und die Besucher müssen hier umkehren und den gleichen Weg zurück.', 'Jochen Duckeck', NULL, NULL, NULL),
('007-007.jpg', NULL, 'Schertelshöhle', 'The Right Branch', 'Der rechte Gang', 'This formation divides the right branch into two parallel passages, which join again after a few meters.', 'Diese Tropfsteingruppe teilt den Gang in zwei parallele Gänge, die sich nach einigen Metern wieder vereinigen.', 'Jochen Duckeck', NULL, NULL, NULL),
('272-007.jpg', NULL, 'Charlottenhöhle', 'Cave Sign', 'Höhlenschild', 'The sign to Charlottes’ cave.\r\nFrom here its 15min walk uphill to the cave.', 'Das Hinweisschild der Charlottenhöhle.\r\nVon hier sind es 15 Minuten Fußweg den Berg hinauf zum Höhleneingang.', 'Jochen Duckeck', NULL, NULL, NULL),
('272-012.jpg', NULL, 'Charlottenhöhle', 'Bear Polish', 'Bärenschliff', 'One of the remains of the cave bears. They lived for a long time in the cave, so the walls got polished by bears walking by time after time.', 'Eines der Überbleibsel der Höhlenbären. Sie lebten so lange in der Höhle, daß die Wände im Vorbeilaufen glattgeschliffen wurde. Diese glatten Stellen heißen <b>Bärenschliff</b>.', 'Jochen Duckeck', NULL, NULL, NULL),
('272-011.jpg', NULL, 'Charlottenhöhle', 'Cvae Wall', 'Höhlenwand', 'Typical dripstone on the caves walls.\r\nCalzite crusts looking like moonmilk.', 'Typischer Wandsinter.\r\nErinnert etwas an Montmilch.', 'Jochen Duckeck', NULL, NULL, NULL),
('272-018.jpg', NULL, 'Charlottenhöhle', 'The First Electric Light', 'Die erste elektrische Beleuchtung.', 'A remains of the first electric light, wires and isolators. \r\nThe wires had no isolation and were thus installed below the ceiling, out of the reach of visitors.\r\nTheye were mounted with isolators on iron bars.', 'Die erste elektrische Beleuchtung wurde, wie damals üblich, mit unisolierten Drähten verlegt.\r\nDie Kabel wurden direkt unter die Decke montiert, außerhalb der Reichweite der Besucher.', 'Jochen Duckeck', NULL, NULL, NULL),
('272-058.jpg', NULL, 'Charlottenhöhle', 'Radish Stalactites', 'Rettich Stalaktiten', 'This small stalactites resemble radishes, seen from below.\r\nThey are typical for the cave.', 'Die kleinen Stalaktiten erinnern an Rettiche von unten gesehen.\r\nSie sind typisch für diese Höhle.', 'Jochen Duckeck', NULL, NULL, NULL),
('272-046.jpg', NULL, 'Charlottenhöhle', 'A Stalagmite', 'Ein Stalagmit', NULL, NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('272-054.jpg', NULL, 'Charlottenhöhle', 'A Typical Passage.', 'Ein typischer Höhlengang', NULL, NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('274-130.jpg', NULL, 'Rhumequelle', 'Main Spring', 'Hauptquelle', 'The green colour of the gypsum rich water.', 'Die grüne Farbe des gipsreichen Wassers.', 'Jochen Duckeck', NULL, NULL, NULL),
('020-075.jpg', NULL, 'Rosenmüllerhöhle', 'Candlelight', 'Bei Kerzenlicht', 'The rails of the path have candle holders, so it is possible to light the cave with candlelight.', 'Die Geländer besitzen in regelmäßigen Abständen Kerzenhalter, die eine stimmungsvolle Beleuchtung der Höhle erlauben. ', 'Jochen Duckeck', NULL, NULL, NULL),
('030-046.jpg', NULL, 'Grand Canyon Caverns', 'Mumified Bobcat', '', 'A mumified bobcat, about 150 years old.', NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('013-017.jpg', NULL, 'Postojnska Jama', 'Entrance', '', 'The entrance of Postojnska Jama. The old entrance building on the right side is from the time, when Slovenia belonged to Austria and the cave was called Adelsberger Grotte.', NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('013-031.jpg', NULL, 'Postojnska Jama', 'The Diamond', '', 'This is a rather famous stalagmite because of its symmetric shape and the bright white calcite.', NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('007-000.jpg', NULL, 'Falkensteiner Höhle', 'Cave Portal', 'Höhlenportal', 'The impressive cave entrance, which was formed by weathering,', 'Das imposante Höhlenportal, das durch Frostverwitterung entstanden ist.', 'Jochen Duckeck', NULL, NULL, NULL),
('070-915.jpg', NULL, 'Aranui Cave', 'Soda Straw', 'Sinterröhrchen', 'Soda straws are formed by a drop of water depositing a circle of calcite.\r\nThis circle grows to form a thin tube.\r\nThe Straw on this image is about 4-5mm in diameter and 30cm long.', 'Sinterröhrchen bilden sich durch einen Wassertropfen, der etwas Kalzit kreisrund um sich herum ablagert.\r\nDer Kreis wird schnell zu einem Röhrchen.\r\nDieses hier hat etwa 4-5mm Durchmesser und ist etwa 30cm lang.', 'Jochen Duckeck', NULL, NULL, NULL),
('383-027.jpg', NULL, 'Alter Elbtunnel', 'Old Elbe Tunnel', 'Tunnelröhre', 'One of the two light blue tunnel tubes, which contain a single car lane and foot paths on both sides.', 'Eine der beiden hellblau gekachelten Röhren die jeweils eine Fahrspur für PKW sowie auf beiden Seiten einen Fußweg besitzen.', 'Jochen Duckeck', NULL, NULL, NULL),
('007-018.jpg', NULL, 'Kocherquelle', 'Kocher Spring', 'Karstquelle', 'A typical Karst Spring on the \r\n<img VSPACE=\"0\" hspace=\"0\" src=\"../../graphics/symbol/Region.gif\"   width=\"12\" height=\"12\">&nbsp;<a href=\"../english/de/region/SchwaebischeAlb.html\">Schw&auml;bische Alb</a> (Swabian Jura), Germany.', 'Eine typische Karstquelle auf der \r\n<img VSPACE=\"0\" hspace=\"0\" src=\"../../graphics/symbol/Region.gif\"   width=\"12\" height=\"12\">&nbsp;<a href=\"../german/de/region/SchwaebischeAlb.html\">Schw&auml;bischen Alb</a>, Deutschland.\r\n', 'Jochen Duckeck', NULL, NULL, NULL),
('159-808.jpg', NULL, 'Hellfire Corner', 'Winston Churchil on the Balcony of Hellfire Corner', '', 'Historic photography of <b class=\"smallCaps\">Winston Churchil</b> on the balcony of Hellfire Corner during World War II.', '', '', '', '', ''),
('159-754.jpg', NULL, 'Dover Castle', 'The Tower', '', 'This tower is the center of the fortification.', '', 'Jochen Duckeck', NULL, NULL, NULL),
('159-805.jpg', NULL, 'Hellfire Corner', 'The White Cliffs of Dover', '', 'The bunker of Hellfire Corner are built into the soft Cretaceous chalk of the White Cliffs of Dover. This is the view from the entrance.', '', 'Jochen Duckeck', NULL, NULL, NULL),
('013-008.jpg', NULL, 'Predjamski Grad', 'Cave Castle Predjama', 'Höhlenschloß Lueg', 'The cave castle of Predjama is definitely the most impressive cave castle of the world.\r\nIt uses an impressive cave portal in the middle of a cliff face.', 'Das Höhlenschloß Lueg ist sicherlich die eindrucksvollste Höhlenburg überhaupt.\r\nDas liegt auch an der Lage des großen Höhlenportals in eine steilen Felswand.', 'Jochen Duckeck', NULL, NULL, NULL),
('159-713.jpg', NULL, 'Kents Cavern', 'Dripstone Layers', '', '', '', 'Jochen Duckeck', '', '', ''),
('013-084.jpg', NULL, 'Škocjanske Jame', 'The Dolina', '', 'The cave below the town Škocjan (St. Kanzian). The cave collapsed on both sides of the town, the town is built on a natural bridge. This is the view through this natural bridge, from one dolina to the next.', '', 'Jochen Duckeck', NULL, NULL, NULL),
('378-248.jpg', NULL, 'Pe&#351;tera Ur&#351;ilor', 'A Colourful formation', 'Eine farbige Formation', NULL, NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('F004-018.jpg', NULL, 'Postojnska Jama', 'Historic Engraving', 'Historischer Stich', 'Postojnska Jama is the most famous cave of the world, and the oldest show cave.\r\nTher are thousands of historic images of the cave.', '', 'unknown', NULL, NULL, NULL),
('013-026.jpg', NULL, 'Postojnska Jama', 'Cave Formation', 'Tropfsteingruppe', 'A huge formation in the cave.', 'Eine imposante Tropfsteingruppe.', 'Jochen Duckeck', NULL, NULL, NULL),
('288-1025.jpg', NULL, 'Thurston Lava Tube', 'Tunnel with Roots', 'Tunnel mit Wurzeln', 'A short lava tube in Volcanoes NP, Big Island, Hawai’i, USA.\r\nThe tube is located under tropical rain forrest, and about 200m are accesible for the public. The cave has a good path and electric light.\r\nBut it is not guided, everybody may visit it on a self guided tour.', 'Ein kurzer Lavatunnel im Volcanoes NP, Big Island, Hawai’i, USA.\r\nDie Höhle befindet sich unter tropischem Regenwald und etwa 200m der Höhle sind für Besucher zugänglich.\r\nDie Höhle hat einen befestigten Weg und elektrisches Licht.\r\nAber es gibt keine Höhlenführungen, Parkbesucher können die Höhle frei besuchen.', 'Jochen Duckeck', NULL, NULL, NULL),
('159-207.jpg', NULL, 'Cheddar Gorge', 'The Gorge', '', 'Steep limestone cliffs containing numerous caves.', '', 'Jochen Duckeck', NULL, NULL, NULL),
('255-112.jpg', NULL, 'Bären- und Karlshöhle', 'Stalactites', 'Stalaktiten', 'This part of the ceiling shows strange stalactites, resembling radishes.', 'Dieser Teil der Decke ist von seltsam aussehenden Stalaktiten bedeckt, die an Rettiche erinnern.', 'Jochen Duckeck', NULL, NULL, NULL),
('F028-001.jpg', NULL, 'Great Britain', 'Geologic Map', '', '', '', 'Horace B. Woodward', '', '', ''),
('274-088.jpg', NULL, '', '', '', NULL, NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('274-090.jpg', NULL, 'Einhornhöhle', 'Interesting Formed Shaft', 'Schacht mit interessantem Aussehen', 'The cave has several similar looking shafts, which seem to be formed by phreatic solution.', 'Die Höhle besitzt mehrer derartige Schächte, die aussehen als seien sie durch phreatische Lösungsprozesse entstanden.', 'Jochen Duckeck', NULL, NULL, NULL),
('274-123.jpg', NULL, '', '', '', NULL, NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('244-098.jpg', NULL, 'Steinkirche', 'Board with Explanations', 'Tafel mit Erklärungen', 'This board with some explanations about the cave can be found at the cave.\r\nIt tells some facts about the cave.\r\nThe cave was used by hunters as shelter during the Old Stone Age.\r\nRemains like flint tools, part of a needle made of bone and other pieces of bone were found here.', 'Dies Tafel mit erklärungen befinset sich am Eingang der Steinkirche.', 'Jochen Duckeck', NULL, NULL, NULL),
('244-110.jpg', NULL, 'Steinkirche', 'Artificial Sidepassage', 'Künstlicher Seitengang', 'This small passage leads through the wall which borders the courtyard in front of the Steinkirche. Although probably originally natural, it is so heavily altered that it seems artificial now.', 'Dieser Durchgang durchbricht die begrenzende Wand des Vorplatzes. Obwohl vielleicht ursprünglich ein natürlicher Durchgang existierte ist davon nach massiven Veränderungen nichts mehr zu sehen, der Durchgang scheint künstlich zu sein.', 'Jochen Duckeck', '', '', ''),
('D017-017.jpg', NULL, 'Olm (<i>Proteus anguinus</i>)', 'Human Fish', 'Grottenolm', '<i>Proteus anguinus</i> is an amphibic salamander, living only in the Dinaric Karst, the karst areas from Trieste in Italy to Montenegro.', 'Der Grottenolm ist ein amphibischer Salamander, der ausschließlich in den Karstgebieten zwischen Triest und Montenegro vorkommt.\r\n', 'Helmut Schlierf', NULL, NULL, NULL),
('257-173.jpg', NULL, 'Immendingen', 'The Danube Sink', 'Donauversickerung', 'The Danube sink, here not complete, is one of the most interesting karst features in southern Germany.\r\nThe water is swallowed by holes in the ground and reappears far away in the Aachtopf spring.\r\nMost time of the year the amount of vanishing water is lower than the Danube, but some months in summer the danube may completely fall dry.', 'Dir Donauversickerung, hier keine vollständige Versickerung, ist eine der interessantesten Karsterscheinungen in Süddeutschland.\r\nDas Wasser wird von Schlucklöchern im Flußbett aufgenommen und taucht ein ganzes Stück entfernt im Aachtopf wieder auf.\r\nDie meiste Zeit des Jahres ist die versickerte Menge kleiner als die Wasserführung der Donau, aber in den Sommermonaten kann es auch zu einem vollständigen Versickern des Wassers kommen.', 'Jochen Duckeck', NULL, NULL, NULL),
('D032-013.jpg', NULL, '', '', '', NULL, NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('013-129.jpg', NULL, 'Zupanova Jama - Taborska Jama', 'Stalagmite', '', 'The cave has many columns, stalagmite that grow rather fast and very thin. They are 10 cm in diameter ant up to 5 m high.', '', 'Jochen Duckeck', NULL, NULL, NULL),
('013-124.jpg', NULL, 'Zupanova Jama - Taborska Jama', 'Cave Entrance', '', 'The entrance to Taborska Jama is a crack in the limestone cliff.', '', 'Jochen Duckeck', NULL, NULL, NULL),
('013-146.jpg', NULL, 'Zupanova Jama - Taborska Jama', 'Stalactite', '', 'A beautiful stalactite. \r\nRather typical for stalactites is, to grow along a crack in the ceiling.\r\nTypical for this cave are the reflections on the surface, caused by single calcite crystals.', '', 'Jochen Duckeck', NULL, NULL, NULL),
('013-155.jpg', NULL, 'Zupanova Jama - Taborska Jama', 'A Mountain of Stalagmites.', '', 'A mountain of stalagmites.\r\nColumns, pilars and stalagmites forming a calcite castle.', '', 'Jochen Duckeck', NULL, NULL, NULL),
('013-222.jpg', NULL, 'Grotte di Castellana', 'Strange Stalagmites in the White Chamber', '', 'The stalagmites in the White Chamber have a strange form.\r\nThey look like a club, with thick ends.\r\nSome of the stalagmites are covered with helictites.', '', 'Jochen Duckeck', NULL, NULL, NULL),
('378-094.jpg', NULL, '', '', '', NULL, NULL, 'Jochen Duckeck', NULL, NULL, NULL),
('F013-001.jpg', NULL, 'Atomkeller', 'Eve of Destruction', 'Vorabend der Vernichtung', 'Soldiers of the ALSOS mission playing with uranium cubes.\r\nThe cubes were used to build the worlds first uranium reactor, but were hidden under the gras in front of the Atomkeller.\r\nThe soldiers collected them and built pyramids, not knowing about radioactive radiation or critical mass.\r\nIf the amount of uranium would have been a bit higher, Haigerloch would be the location of the first atomic chain reaction, not Horoshima. ', 'Soldaten der ALSOS Mission spielen mit Würfeln aus angereichertem Uran.\r\nDie Würfel wurden zum Bau des ersten Uran Reaktors benutzt, waren aber unter der Wiese vor dem Atomkeller versteckt worden.\r\nDie Soldaten suchten die Würfel zusammen und bauten eine Pyramide daraus, ohne von radioaktiver Strahlung oder kritischer Masse gehört zu haben.\r\nWenn der Gehalt an Uran etwas höher gewesen wäre, wäre Haigerloch der Ort der ersten atomaren Kettenreaktion, nicht Hiroshima.', 'unknown', NULL, NULL, NULL),
('F031-001.jpg', NULL, 'Lindachhöhle', 'After a Surveying Tour', 'Nach der Vermessungstour', 'After a surveying tour with some friends into the small Lindachhöhle\r\nnear Bolheim, South Germany.', 'Nach einer Vermessungstour mit Freunden in die Lindachhöhle bei Bolheim, Schwäbische Alb.', 'Bernhard Mangold', '', '', ''),
('F004-013.jpg', NULL, 'Tilly Whim Caves', 'The Quarry Works', '', 'A drawing by <b class=\"smallCaps\">Thomas Webster</b> (1816) of the quarry works at Tilly Whim Caves.', '', 'Thomas Webster', NULL, NULL, NULL),
('D017-049.jpg', NULL, 'Planinska Jama', 'Behind the First Turn', '', 'Behind the first turn of the huge cave passage, a view back shows a magical combination of daylight and its reflection in the limestone rich water.\r\n', '', 'Helmut Schlierf', NULL, NULL, NULL),
('013-311.jpg', NULL, '', '', '', NULL, NULL, 'Jochen Duckeck', NULL, NULL, NULL),
